package methods;

public class Method1 
{
	public void m1()
	{
		int n=5;
		int sum=1;
		for(int i=1;i<=n;i++)
		{
			sum*=i;
		}
		
		System.out.println(sum);
	}

	public static void main(String[] args)
	{
		// TODO Auto-generated method stub
		int a=3;
		Method1 m=new Method1();
		m.m1();

	}

}















































































































































































































